import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import { Dialog, Slide } from '@material-ui/core';
import MuiDialogTitle from '@material-ui/core/DialogTitle';
import MuiDialogContent from '@material-ui/core/DialogContent';
import MuiDialogActions from '@material-ui/core/DialogActions';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import { Typography, Grid, TextField } from '@material-ui/core';
import stringConstants from './stringConstants.jsx';

import useStyles from './styles.jsx'

import {loginUser} from './service.js';



const styles = (theme) => ({
    root: {
        margin: 0,
        padding: theme.spacing(2),
    },
});

const DialogTitle = withStyles(styles)((props) => {
    const { children, classes, onClose, ...other } = props;
    return (
        <MuiDialogTitle disableTypography className={classes.root} {...other}>
            <Typography variant="h6">{children}</Typography>
            {onClose ? (
                <IconButton aria-label="close" className={classes.closeButton} onClick={onClose}>
                    <CloseIcon />
                </IconButton>
            ) : null}
        </MuiDialogTitle>
    );
});

const DialogContent = withStyles((theme) => ({
    root: {
        padding: theme.spacing(2),
    },
}))(MuiDialogContent);

const DialogActions = withStyles((theme) => ({
    root: {
        margin: 0,
        padding: theme.spacing(1),
    },
}))(MuiDialogActions);


function UpdateRecordPopup(props) {

    const [open, setOpen] = React.useState( ' ');



    const [effort, seteffort] = React.useState( editRowId ? editRowId[effort] : ''  );
    const [description, setdescription] = React.useState( editRowId ? editRowId[description] : ''  );
    const [activityType, setactivityType] = React.useState( editRowId ? editRowId[activityType] : '' );
    const [serviceElement, setserviceElement] = React.useState( editRowId ?editRowId[serviceElement]: ''  );
    const [assyst, setassyst] = React.useState( editRowId ? editRowId[assyst]  : '' );
    const [company, setcompany] = React.useState( editRowId ? editRowId[company] : ''  );
    const [address, setaddress] = React.useState( editRowId ?editRowId[address] : ''  );


    const [errorfield, seterrorfield] = React.useState(-1);
    const [login, setlogin] = React.useState(false);


    const [pageNumber, setPageNumber] = React.useState(0);

    const {editRowId : fieldValues} = props


    React.useEffect(() => {setOpen(true);console.log(fieldValues,'fieldValues')} , [])

    const classes = useStyles();


    const handleTextFieldChange = (val, type) => {
        switch (type) {
            case "Name": { seteffort(val); break; }
            case "Phone": { setdescription(val); break; }
            case "Gmail": { setassyst(val); break; }
            case "activityType": { setactivityType(val); break; }
            case "serviceElement": { setserviceElement(val); break; }
            case "Company Name": { setcompany(val); break; }
            case "ADDRESS": { setaddress(val); break; }
            default:
                break;
        }
    }

    const handleLogin = () => {

        if(!pageNumber) setPageNumber(1);

        else{

            if (!effort || effort.length < 4)
               seterrorfield(0)
            else if (!activityType || activityType.length < 4)
              seterrorfield(1)
            else{

                loginUser().then(res => {
                    console.log(res,"res")

                    if(res)
                        // setUserEffort(res.useEffort)

                    setOpen(false)
                })

            }


        }



    }

    const handleRegister = (type) => {

        if(pageNumber) setPageNumber(0)

        if (!effort || effort.length < 4)
            seterrorfield(0)
        else if (!description || description.length < 4)
            seterrorfield(1)
        else if (!assyst || assyst.length < 4)
            seterrorfield(4)
        else if (!company || company.length < 4)
            seterrorfield(5)
        else if (!address || address.length < 4)
            seterrorfield(6)
        else if (effort && description && (type == 'register')) {
            // let getRegister = props.registered;
            // var error = false;
            // getRegister.forEach(e => {
            //     if (e.assyst === assyst || e.description === description) {
            //         if (e.assyst === assyst) seterrorfield(4)
            //         else if (e.description === description) seterrorfield(1)
            //         error = true;
            //         return;
            //     }
            // })
            // if (!error) {
            //     let arr = {
            //         'description': description,
            //         'effort': effort,
            //         'activityType': activityType,
            //         'assyst': assyst,
            //         'company': company,
            //         'address': address
            //     };

            //     getRegister.push(arr);
            //     props.setRegister(getRegister)
            //     let a = [];
            //     a.push(arr)
            //     props.isLoggedIn(a)
            // }
        }
        // if (effort &&  activityType && (type == 'login')) {}
    }

    const StyledButton = withStyles({
        root: {
            background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
            borderRadius: 3,
            border: 0,
            color: 'white',
            height: 48,
            padding: '0 30px',
            boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
        },
        label: {
            textTransform: 'capitalize',
        },
    })(Button);



    const getValue = (type,index) => {

        if(type === 'register')
            switch(index){
                case "0":{
                    return effort
                }
                case "1":{
                    return description
                }
                case "2":{
                    return activityType
                }
                case "3":{
                    return serviceElement
                }
                case "4":{
                    return assyst
                }
                case "5":{
                    return company
                }
                case "5":{
                    return address
                }
            }
        else
        switch(index){
            case "0":{
                return effort
            }
            case "1":{
                return activityType
            }
        }


    }

    const getErrorMsg = () => {
        switch(errorfield){
            case  0 :return 'Enter Valid Name !'
            case  1 :return 'Enter Valid Number !'
            case  2 :return 'Enter Valid Password !'
            case  3 :return 'Enter Same Password !'
            case  4 :return 'Enter Valid Email'
            case  5 :return 'Enter Valid Company'
            case  6 :return 'Enter Valid Address'
        }
    }


    return (
        <div>
            <Dialog 
                aria-labelledby="customized-dialog-title" open={open}>
                <DialogTitle id="customized-dialog-title" style={{ background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)' }} >
                   Edit Record
             </DialogTitle>

                <DialogContent dividers>

                    <form className={classes.rootregister}>

                        { stringConstants.EDIT_POPUP.map((object, index) => <TextField key={index} id={object.id} type={object.type} error={index === errorfield ? true : 0}
                            helperText={ (index === errorfield) ? getErrorMsg() : ''} required label={object.label} color="primary" onChange={(e) => handleTextFieldChange(e.target.value, object.id)} 
                            value = { fieldValues[object.id] } />
                        ) 

                        }

                    </form>
                </DialogContent>
                <DialogActions>

                    <Grid container>
                        <Grid item md={6} lg={6} sm={6} xs={6} >
                            <StyledButton onClick={() => { setlogin(false);  handleRegister(); }} color="primary">
                                Update
                     </StyledButton>
                        </Grid>
                        <Grid item md={6} lg={6} sm={6} xs={6} >
                            <Grid container justify="flex-end" alignContent="center" >
                                <StyledButton color="primary" onClick={e => {  setlogin(true);  handleLogin() ;}}>
                                     Delete
                             </StyledButton>
                            </Grid>
                        </Grid>
                    </Grid>

                </DialogActions>
            </Dialog>

        </div>
    );
}



export default (UpdateRecordPopup)
