import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import { Dialog, Slide } from '@material-ui/core';
import MuiDialogTitle from '@material-ui/core/DialogTitle';
import MuiDialogContent from '@material-ui/core/DialogContent';
import MuiDialogActions from '@material-ui/core/DialogActions';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import { Typography, Grid, TextField } from '@material-ui/core';
import stringConstants from './stringConstants.jsx';

import useStyles from './styles.jsx'

import {loginUser} from './service.js';

import { makeStyles } from '@material-ui/core/styles';

import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';

const useStylesselect = makeStyles((theme) => ({
    formControl: {
      margin: theme.spacing(1),
      minWidth: 120,
    },
    selectEmpty: {
      marginTop: theme.spacing(2),
    },
  }));



const styles = (theme) => ({
    root: {
        margin: 0,
        padding: theme.spacing(2),
    },
});

const DialogTitle = withStyles(styles)((props) => {
    const { children, classes, onClose, ...other } = props;
    return (
        <MuiDialogTitle disableTypography className={classes.root} {...other}>
            <Typography variant="h6">{children}</Typography>
            {onClose ? (
                <IconButton aria-label="close" className={classes.closeButton} onClick={onClose}>
                    <CloseIcon />
                </IconButton>
            ) : null}
        </MuiDialogTitle>
    );
});

const DialogContent = withStyles((theme) => ({
    root: {
        padding: theme.spacing(2),
    },
}))(MuiDialogContent);

const DialogActions = withStyles((theme) => ({
    root: {
        margin: 0,
        padding: theme.spacing(1),
    },
}))(MuiDialogActions);


function UpdateRecordPopup(props) {

    const classesSelect = useStylesselect();

    const {editRowId : fieldValues,openAddPopUp} = props


    const [open, setOpen] = React.useState(true);


    const [effort, seteffort] = React.useState( fieldValues ? fieldValues["effort"] : ''  );
    const [description, setdescription] = React.useState( fieldValues ? fieldValues["description"] : ''  );
    const [activityType, setactivityType] = React.useState( fieldValues ? fieldValues["activityType"] : '' );
    const [serviceElement, setserviceElement] = React.useState( fieldValues ?fieldValues["serviceElement"]: ''  );
    const [assyst, setassyst] = React.useState( fieldValues ? fieldValues["assyst"]  : '' );



    const [company, setcompany] = React.useState( fieldValues ? fieldValues["company"] : ''  );
    const [address, setaddress] = React.useState( fieldValues ?fieldValues["address"] : ''  );


    const [errorfield, seterrorfield] = React.useState(-1);
    const [login, setlogin] = React.useState(false);


    const [pageNumber, setPageNumber] = React.useState(0);



    React.useEffect(() => {setOpen(true);console.log(fieldValues,'fieldValues')} , [])

    React.useEffect(() => {setOpen(true);console.log(fieldValues,'fieldValues')} , [])





    const classes = useStyles();


    const handleTextFieldChange = (val, type) => {
        switch (type) {
            case "Name": { seteffort(val); break; }
            case "Phone": { setdescription(val); break; }
            case "Gmail": { setassyst(val); break; }
            case "activityType": { setactivityType(val); break; }
            case "serviceElement": { setserviceElement(val); break; }
            case "Company Name": { setcompany(val); break; }
            case "ADDRESS": { setaddress(val); break; }
            default:
                break;
        }
    }



    const StyledButton = withStyles({
        root: {
            background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
            borderRadius: 3,
            border: 0,
            color: 'white',
            height: 48,
            padding: '0 30px',
            boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
        },
        label: {
            textTransform: 'capitalize',
        },
    })(Button);



    const getValue = (type,index) => {
            switch(index){
                case 0:{
                    return effort
                }
                case 1:{
                    return description
                }
                case 2:{
                    return activityType
                }
                case 3:{
                    return serviceElement
                }
                case 4:{
                    return assyst
                }
                case 5:{
                    return company
                }
            }
    }

    const getErrorMsg = () => {
        switch(errorfield){
            case  0 :return 'Enter Valid Name !'
            case  1 :return 'Enter Valid Number !'
            case  2 :return 'Enter Valid Password !'
            case  3 :return 'Enter Same Password !'
            case  4 :return 'Enter Valid Email'
            case  5 :return 'Enter Valid Company'
            case  6 :return 'Enter Valid Address'
        }
    }


    const handleChange = () => {}


    return (
        <div>
            <Dialog 
                 onClose={() => setOpen(false)}
                aria-labelledby="customized-dialog-title" open={open}>
                <DialogTitle id="customized-dialog-title" style={{ background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)' }}  >
                   Edit Record
             </DialogTitle>

                <DialogContent dividers>

                    <form className={classes.rootregister}>

                        { stringConstants.EDIT_POPUP.map((object, index) => <TextField key={index} id={object.id} type={object.type} error={index === errorfield ? true : 0}
                            helperText={ (index === errorfield) ? getErrorMsg() : ''} required label={object.label} color="primary" onChange={(e) => handleTextFieldChange(e.target.value, object.id)} 
                            value = { getValue(object,index) } />
                        ) 

                        }
<FormControl required className={classesSelect.formControl}>
        <InputLabel htmlFor="age-native-required">serviceElement</InputLabel>
        <Select
          native
          value={10}
          onChange={() => handleChange()}
          name="age"
          inputProps={{
            id: 'age-native-required',
          }}
        >
          <option aria-label="None" value="" />
          <option value={10}>Ten</option>
          <option value={20}>Twenty</option>
          <option value={30}>Thirty</option>
        </Select>
        <FormHelperText>Required</FormHelperText>
      </FormControl>

      <FormControl required className={classesSelect.formControl}>
        <InputLabel htmlFor="age-native-required">ActivityTypeâ€‰</InputLabel>
        <Select
          native
          value={10}
          onChange={() => handleChange()}
          name="age"
          inputProps={{
            id: 'age-native-required',
          }}
        >
          <option aria-label="None" value="" />
          <option value={10}>Ten</option>
          <option value={20}>Twenty</option>
          <option value={30}>Thirty</option>
        </Select>
        <FormHelperText>Required</FormHelperText>
      </FormControl>

                    </form>
                </DialogContent>
                <DialogActions>

                    <Grid container>
                        <Grid item md={6} lg={6} sm={6} xs={6} >
                            <StyledButton onClick={() => { setlogin(false);   }} color="primary">
                                Update
                     </StyledButton>
                        </Grid>
                        <Grid item md={6} lg={6} sm={6} xs={6} >
                            <Grid container justify="flex-end" alignContent="center" >
                                <StyledButton color="primary" onClick={e => {  setlogin(true); }}>
                                     Delete
                             </StyledButton>
                            </Grid>
                        </Grid>
                    </Grid>

                </DialogActions>
            </Dialog>

        </div>
    );
}



export default (UpdateRecordPopup)
